
public class Eje02PerimetroCircunferencia {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// el perimetro de un circunferencia es P=2*PI*R
		
		final double PI=3.14159;
		double radio=5;
		
		double perimetro=2*PI*radio;
		
		System.out.println("El perimetro de la circunferencia de radio 5 es: "+perimetro);
		
		
		
		

	}

}
