
public class UsoVariables {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		byte edad; //declarion de variables
		edad=17; //inicializacion de varibles
		byte edad2=18;
		
		System.out.println(edad); //el println hace el salto de linea
		System.out.println(edad2);
					
		// declaraciones multiples y constantes
		System.out.println("**************************declaraciones multiples************************");
		int operador1, operador2, resultado;
		
		operador1=7;
		operador2=9;
		resultado=operador1+operador2;
		System.out.println(resultado);
		
		int num1=10,num2=10,res=num1+num2;
		System.out.print(res);
		
		
	}

}
